import { useState, useEffect } from 'react';
import CountryInfo from './component/CountryInfo'
import Table from './component/Table'
import './App.css';
import axios from 'axios'

function App() {

  const [countries, setCountries] = useState([])
  const [rates, setRates] = useState([])
  const [filterCountry, setFilterCountry] = useState('')
  const [visibility, setVisibility] = useState(false)


  useEffect(() => {
    const fetchData = async () => {
      const headers = await axios('http://localhost:3001')
      const headersArr = headers.data.map(header => header.country_name)
      setFilterCountry(headersArr)
      console.log(headersArr)
    }
    fetchData()
  }, [])

  function handleSubmit(e) {
    e.preventDefault();
    const postData = async () => {
      try {
        const countriesResult = await axios.post('http://localhost:3001/countries', {
          country_name1: filterCountry1,
          country_name2: filterCountry2
        })
        console.log(countriesResult.data)
        setCountries(countriesResult.data)
        const ratesResult = await axios.post('http://localhost:3001/rates', {
          country_name1: filterCountry1,
          country_name2: filterCountry2
        })
        console.log(ratesResult.data)
        setRates(ratesResult.data)
      } catch (err) {
        console.log(err)
      }
    }
    postData()
    setVisibility(true)
  }

  function handleFiterCountry1(e) {
    if (e.target.value === filterCountry2) {
      setFilterCountry1(e.target.value)
      alert("Please choose a different country.")
    } else {
      setFilterCountry1(e.target.value)
    }
  }

  function handleFiterCountry2(e) {
    if (e.target.value === filterCountry1) {
      setFilterCountry2(e.target.value)
      alert("Please choose a different country.")
    } else {
      setFilterCountry2(e.target.value)
    }
  }


  const countriesCodes = countries.map(country => country.country_name)

  return (
    <div className="App">
      <h1 className="table-name">Countries and Exchange rates</h1>
      <form className="select-form" onSubmit={handleSubmit}>
        <select value={filterCountry1} onChange={handleFiterCountry1}>
          {countriesCodes.map((field, index) => {
            return <option key={index} value={field}>{field}</option>
          })}
        </select>
        <select value={filterCountry2} onChange={handleFiterCountry2}>
          {countriesCodes.map((field, index) => {
            return <option key={index} value={field}>{field}</option>
          })}
        </select>
        <input type="submit" value='Submit' />
      </form>
      <hr />
      {visibility ?
        <div className="container-results">
          <div className="country-info">
            <CountryInfo
              countries={countries}
              filterCountry={filterCountry1}
            />
            <CountryInfo
              countries={countries}
              filterCountry={filterCountry2}
            />
          </div>
          <div className="rate-table">

          </div>
        </div> : null
      }
    </div>
  );
}

export default App;
