import "../App.css"

export default function CountryInfo({ countries, filterCountry }) {
  const country = countries.filter(counrty => counrty.country_code === filterCountry)
  console.log("VVVVVV : ", country)
  return (
    <>
      <p><b>Name: </b><span>{country.country_name}</span></p>
      <p><b>Calling Code: </b><span>{country.calling_code}</span></p>
      <p><b>Capital: </b><span>{country.capital}</span></p>
      <p><b>Region: </b><span>{country.region}</span></p>
    </>
  )
}
