import { useState, useEffect } from 'react';
import CountryInfo from './component/CountryInfo'
import './App.css';
import axios from 'axios'

function App() {

  const [countries, setCountries] = useState([])
  const [rates, setRates] = useState([])
  const [filterCountry1, setFilterCountry1] = useState('China')
  const [filterCountry2, setFilterCountry2] = useState('Brazil')
  const [visibility, setVisibility] = useState(false)


  useEffect(() => {
    const fetchData = async () => {
      const countriesResult = await axios('http://localhost:3001/countries')
      const ratesResult = await axios('http://localhost:3001/rates')
      setCountries(countriesResult.data)
      setRates(ratesResult.data)
      console.log(countriesResult.data)
      console.log(ratesResult.data)
    }
    fetchData()
  }, [])

  function handleSubmit(e) {
    e.preventDefault();
    setVisibility(true)
  }

  function handleFiterCountry1(e) {
    if (e.target.value === filterCountry2) {
      setFilterCountry1(e.target.value)
      alert("Please choose a different country.")
    } else {
      setFilterCountry1(e.target.value)
    }

  }

  const countriesCodes = countries.map(country => country.country_name)

  return (
    <div className="App">
      <h1 className="table-name">Countries and Exchange rates</h1>
      <form className="select-form" onSubmit={handleSubmit}>
        <select value={filterCountry1} onChange={handleFiterCountry1}>
          {countriesCodes.map((field, index) => {
            return <option key={index} value={field}>{field}</option>
          })}
        </select>
        <select value={filterCountry2} onChange={e => { setFilterCountry2(e.target.value) }}>
          {countriesCodes.map((field, index) => {
            return <option key={index} value={field}>{field}</option>
          })}
        </select>
        <input type="submit" value='Submit' />
      </form>
      <hr />
      {visibility ?
        <div className="container-results">
          <div className="country-info">
            <CountryInfo
              countries={countries}
              filterCountry={filterCountry1}
            />
            <CountryInfo
              countries={countries}
              filterCountry={filterCountry2}
            />
          </div>
          <div className="rate-table">
          </div>
        </div> : null
      }


    </div>
  );
}

export default App;
