import { useState, useEffect } from 'react';
import './App.css';
import axios from 'axios'

function App() {

  const [countries, setCountries] = useState([])
  const [rates, setRates] = useState([])
  const [filterCountry1, setFilterCountry1] = useState('')
  const [filterCountry2, setFilterCountry2] = useState('')

  useEffect(() => {
    const fetchData = async () => {
      const countriesResult = await axios('http://localhost:3001/countries')
      const ratesResult = await axios('http://localhost:3001/rates')
      setCountries(countriesResult.data)
      setRates(ratesResult.data)
    }
    fetchData()
  }, [])

  console.log(countries)
  console.log(rates)

  return (
    <div className="App">
      <h1 className="table-name">Countries and Exchange rates</h1>
      <div className="container">
        <div className="select-form">
          <input type="text"
            name="filterCountry1"
            placeholder='choose a country'
            value={filterCountry1}
            onChange={e => { setFilterCountry1(e.target.value) }}
          />
          <input type="text"
            name="filterCountry2"
            placeholder='choose a country'
            value={filterCountry2}
            onChange={e => { setFilterCountry2(e.target.value) }}
          />
        </div>
        <div className="rate-table">
        </div>
        <div className="country-info">
        </div>
      </div>

    </div>
  );
}

export default App;
